mapper.update:
	  	{ call
  		declare
  		begin
  		UPDATE COMMUNITY
  		SET TITLE = #{title}, CONTENT = #{content}, REGDATE = SYSDATE
  		WHERE COM_SEQ = #{com_seq};
  		UPDATE COMM_INTERIOR
  		SET FILE_IMAGE = #{file_image}
  		WHERE COM_SEQ = #{com_seq};
  		END
  		}

UPDATE COMMUNITY
SET HIT = HIT + 1
WHERE COM_SEQ = #{com_seq}

mapper.delete:
		{ call
  		declare
  		begin
  		DELETE FROM COMMUNITY
  		WHERE COM_SEQ = #{com_seq};
  		DELETE FROM COMM_INTERIOR
  		WHERE COM_SEQ = #{com_seq};
  		END
  		}

mapper.update:
UPDATE liketo
SET like_check = like_check + 1 
WHERE mno = #{mno} AND boardno = #{boardno}

UPDATE liketo
SET like_check = 0
WHERE mno = #{mno} AND boardno = #{boardno}
